import './todoListDisplay.css';
import React, { useState } from 'react';

const TodoListDisplay = (props) => {
  const [editedText, setEditedText] = useState('');
  const [editingNoteId, setEditingNoteId] = useState(null);

  const startEditing = (id) => {
    setEditingNoteId(id);
    setEditedText(props.notes.find(note => note.id === id)?.description || '');
  };

  const updateClick = (id,updatedDescription) => {
    // Call the appropriate API or function to update the note with the new text
    console.log(`Updated text for note with id ${id}: ${editedText}`);
    fetch(props.API_URL + "api/todoapp/UpdateNotes",{
      method: "PUT",
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        id: id,
        updatedDescription: editedText,
      }),
    })
    .then(response => response.json())
    .then(result => {
      alert(result);
      setEditingNoteId(null); // Reset editing after updating
      })
    .catch(error => {
      console.error('Error updating note:', error);
    // Handle errors as needed
  });
  };

  const deleteClick = (id) => {
    fetch(props.API_URL + "api/todoapp/DeleteNotes?id=" + id, {
      method: "DELETE",
    })
      .then(res => res.json())
      .then(result => {
        alert(result);
        props.refreshNotes();
      });
  };

  return (
    <div>
      {props.notes.map(note =>
        <div key={note.id} className="topicTitle">
          <input
            type="text"
            value={editingNoteId === note.id ? editedText : note.description}
            onFocus={() => startEditing(note.id)}
            onChange={(e) => setEditedText(e.target.value)}
          />
          <div className="topicAction">
            <button className='updateBtn' onClick={() => updateClick(note.id)}>Update Notes</button>
            <button className='deleteBtn' onClick={() => deleteClick(note.id,editedText)}>Delete Notes</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default TodoListDisplay;
