import './todoListDisplay.css';
import React, { useState, useEffect, useRef} from 'react';


const TodoListDisplay = (props)=>{
    const [editedText, setEditedText] = useState('');
    useEffect(() => {
      if (inputRef.current) {
        inputRef.current.focus();
      }
    }, [editedText]);
    const updateClick = (id) =>{
      console.log(`Updated text for note with id ${id}: ${editedText}`);

    }
    const deleteClick = (id) => {
        fetch(props.API_URL + "api/todoapp/DeleteNotes?id=" + id, {
          method: "DELETE",
        })
          .then(res => res.json())
          .then(result => {
            alert(result);
            props.refreshNotes();
          });
      };

    return(
        <div>
            {props.notes.map(note =>
            <div key={note.id} className="topicTitle">
                {/* <input type="text" value={note.description} /> */}
                <input
            ref={inputRef}
            type="text"
            value={note.id === editedText ? editedText : note.description}
            onFocus={() => setEditedText(note.description)}
            onChange={(e) => setEditedText(e.target.value)}
          />
                <div className="topicAction">
                    <button className='updateBtn' onClick={()=> updateClick(note.id)}>Update Notes</button>
                    <button className='deleteBtn' onClick={() => deleteClick(note.id)}>Delete Notes</button> 
                </div>

            </div>
      )}
        </div>
    );
}
export default TodoListDisplay;
